'use client'

import Link from 'next/link'
import { useContext, useEffect, useState } from 'react'
import { CartContext } from '../Providers'

export default function FilterNav() {
	const [cats, setCats] = useState([])
	const { cartProducts, clearCart } = useContext(CartContext)

	useEffect(() => {
		fetch('/api/categories').then(res => {
			res.json().then(cats => setCats(cats))
		})

		const container = document.getElementById('cats-list')

		const onWheel = e => {
			e.preventDefault()
			container.scrollBy({
				left: e.deltaY < 0 ? -30 : 30,
			})
		}

		container.addEventListener('wheel', onWheel)
		return () => container.removeEventListener('wheel', onWheel)
	}, [])

	cats.map(c => {
		if (c.name == 'Другое') cats.push(cats.splice(cats.indexOf(c), 1)[0])
		if (c.name == 'Пиццы') cats.unshift(cats.splice(cats.indexOf(c), 1)[0])
	})

	return (
		<nav className='sticky top-2 bg-white min-w-full h-[90px] shadow-md flex gap-5 px-10 z-20 '>
			<div
				id='cats-list'
				className='cats-list min-w-full flex gap-5 max-[930px]:overflow-x-scroll max-[930px]:block max-[930px]:whitespace-nowrap max-[930px]:align-middle'
			>
				{cats?.length > 0 &&
					cats.map(c => (
						<Link
							key={c._id}
							className='w-[90px] font-normal text-center pt-2 max-[930px]:inline-block max-[930px]:ml-4'
							href={'/#' + c.name}
						>
							{c.name == 'Напитки' && (
								<div className="bg-[url('data:image/png;base64,')] hover:bg-[url('data:image/png;base64,')] h-[48px] bg-no-repeat bg-center transition-all duration-300 ease-in-out relative after:absolute after:bottom-[-25px] after:left-[13%] after:content-['Напитки'] hover:after:font-bold after:transition-all after:ease-in-out after:duration-300"></div>
							)}

							{c.name == 'Пиццы' && (
								<div className="bg-[url('data:image/png;base64,')] hover:bg-[url('data:image/png;base64,')] h-[48px] bg-no-repeat bg-center transition-all duration-300 ease-in-out relative after:absolute after:bottom-[-25px] after:left-[18%] after:content-['Пиццы'] hover:after:font-bold after:transition-all after:ease-in-out after:duration-300"></div>
							)}
							{c.name == 'Другое' && (
								<div className="bg-[url('data:image/png;base64,')] hover:bg-[url('data:image/png;base64,')] h-[48px] bg-no-repeat bg-center transition-all duration-300 ease-in-out relative after:absolute after:bottom-[-25px] after:left-[18%] after:content-['Другое'] hover:after:font-bold after:transition-all after:ease-in-out after:duration-300"></div>
							)}
							{c.name == 'Закуски' && (
								<div className="bg-[url('data:image/png;base64,')] hover:bg-[url('data:image/png;base64,')] h-[48px] bg-no-repeat bg-center transition-all duration-300 ease-in-out relative after:absolute after:bottom-[-25px] after:left-[13%] after:content-['Закуски'] hover:after:font-bold after:transition-all after:ease-in-out after:duration-300"></div>
							)}
							{c.name == 'Кофе' && (
								<div className="bg-[url('data:image/png;base64,')] hover:bg-[url('data:image/png;base64,')] h-[48px] bg-no-repeat bg-center transition-all duration-300 ease-in-out relative after:absolute after:bottom-[-25px] after:left-[25%] after:content-['Кофе'] hover:after:font-bold after:transition-all after:ease-in-out after:duration-300"></div>
							)}
							{c.name == 'Десерты' && (
								<div className="bg-[url('data:image/png;base64,')] hover:bg-[url('data:image/png;base64,')] h-[48px] bg-no-repeat bg-center transition-all duration-300 ease-in-out relative after:absolute after:bottom-[-25px] after:left-[13%] after:content-['Десерты'] hover:after:font-bold after:transition-all after:ease-in-out after:duration-300"></div>
							)}
							{c.name == 'Соусы' && (
								<div className="bg-[url('data:image/png;base64,')] hover:bg-[url('data:image/png;base64,')] h-[48px] bg-no-repeat bg-center transition-all duration-300 ease-in-out relative after:absolute after:bottom-[-25px] after:left-[20%] after:content-['Соусы'] hover:after:font-bold after:transition-all after:ease-in-out after:duration-300"></div>
							)}
							{![
								'Напитки',
								'Пиццы',
								'Закуски',
								'Кофе',
								'Десерты',
								'Соусы',
								'Другое',
							].includes(c.name) && (
								<div
									className={
										"bg-[url('data:image/png;base64,')] hover:bg-[url('data:image/png;base64,')] h-[48px] bg-no-repeat bg-center transition-all duration-300 ease-in-out relative after:absolute after:bottom-[-25px] after:left-[20%] hover:after:font-bold after:transition-all after:ease-in-out after:duration-300 hover:font-bold"
									}
								>
									<span className='absolute bottom-[-25px] w-full left-0'>
										{c.name}
									</span>
								</div>
							)}
							{/* <span>{c.name}</span> */}
						</Link>
					))}
			</div>

			<Link
				href={'/cart'}
				className='absolute right-2 self-center h-10 text-white bg-primary hover:bg-orange-600 focus:ring-4 focus:outline-none focus:ring-orange-300 font-medium rounded-lg text-sm sm:w-auto px-5 py-2.5 text-center max-[930px]:-bottom-12'
			>
				Корзина ({cartProducts?.length})
			</Link>
			{/* <div>
				<span
					onClick={() => console.log(cartProducts)}
					className='block text-center font-bold cursor-pointer'
				>
					Cart ({cartProducts?.length})
				</span>
				<span
					onClick={clearCart}
					className=' block text-center font-bold cursor-pointer'
				>
					Очистить
				</span>
			</div> */}
		</nav>
	)
}
